// SchemaOption.js
import React from 'react';

const SchemaOption = ({ schema, handleRemoveSchema }) => {
  return (
    <div>
      <select value={schema.value} onChange={(e) => handleSelectChange(e, index)}>
        <option value={schema.value}>{schema.label}</option>
      </select>
      <button onClick={handleRemoveSchema}>Remove</button>
    </div>
  );
};

export default SchemaOption;
