import React, { useState } from 'react';

const App = () => {
  const [isSidebarOpen, setSidebarOpen] = useState(false);
  const [segmentName, setSegmentName] = useState('');
  const [selectedSchema, setSelectedSchema] = useState([]);
  const [newSchemaIndex, setNewSchemaIndex] = useState(0);

  const schemaOptions = [
    { label: 'First Name', value: 'first_name' },
    { label: 'Last Name', value: 'last_name' },
    { label: 'Gender', value: 'gender' },
    { label: 'Age', value: 'age' },
    { label: 'Account Name', value: 'account_name' },
    { label: 'City', value: 'city' },
    { label: 'State', value: 'state' },
  ];

  const toggleSidebar = () => {
    setSidebarOpen(!isSidebarOpen);
  };

  const handleAddSchema = () => {
    let newSchema = null;
    if (newSchemaIndex < schemaOptions.length) {
      newSchema = schemaOptions[newSchemaIndex];
      if (!selectedSchema.some(schema => schema.label === newSchema.label)) {
        setSelectedSchema([...selectedSchema, newSchema]);
      }
      setNewSchemaIndex(newSchemaIndex + 1);
    }
  };

  const handleRemoveSchema = (index) => {
    const updatedSchema = selectedSchema.filter((schema, i) => i !== index);
    setSelectedSchema(updatedSchema);
  };

  const handleSaveSegment = () => {
    toggleSidebar();
  };

  return (
    <div>
      <button onClick={() => toggleSidebar()} className="save-button">
        Save segment
      </button>
      {isSidebarOpen && (
        <div className="sidebar">
          <div>
            <input
              type="text"
              placeholder="Segment name"
              value={segmentName}
              onChange={(e) => setSegmentName(e.target.value)}
            />
          </div>
          <div>
            <button onClick={() => handleAddSchema()}>+ Add new schema</button>
          </div>
          <div className="blue-box">
            {selectedSchema.map((schema, index) => (
              <div key={index}>
                <select value={schema.value}>
                  <option value={schema.value}>{schema.label}</option>
                </select>
                <button onClick={() => handleRemoveSchema(index)}>Remove</button>
              </div>
            ))}
          </div>
          <button onClick={() => handleSaveSegment()}>Save</button>
        </div>
      )}
    </div>
  );
};

export default App;
