import React from 'react';
import SchemaOption from './SchemaOption'; 
import { IoIosArrowBack } from 'react-icons/io';

const Sidebar = ({
  isSidebarOpen,
  segmentName,
  setSegmentName,
  selectedSchema,
  handleAddSchema,
  handleRemoveSchema,
  handleSaveSegment,
  handleCancel,
  handleSelectChange 
}) => {
  return (
    isSidebarOpen && (
      <div className="sidebar">
        <div className='sidebar-head'><h1><IoIosArrowBack size={30} /> Saving  Segment</h1></div>
        <div className='sidebar-cont'>
        <div>
          <p>Enter the Name of Segment</p>
          <input
            type="text"
            placeholder="Segment name"
            value={segmentName}
            onChange={(e) => setSegmentName(e.target.value)}
          />
          <p>To save your segment, you need to add the schemas to build the query</p>
        </div>
        <div className="blue-box">
          {selectedSchema.map((schema, index) => (
            <SchemaOption key={index} schema={schema} handleRemoveSchema={() => handleRemoveSchema(index)} handleSelectChange={handleSelectChange} /> // Pass handleSelectChange here
          ))}
          <button onClick={handleAddSchema}>+ Add new schema</button>
        </div>
        <div>
          <button onClick={handleSaveSegment}>Save</button>
          <button onClick={handleCancel}>Cancel</button>
        </div></div>
      </div>
    )
  );
};

export default Sidebar;
